package com.java.sprintQaClass05;

public class SwapNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		int y = 20;
		
		
		//after swapping x = 20 and y = 10;  
		
		int z;
		z = x;      //10
		x = y;      //20
		y = z;       //10
		
		System.out.println(x);    
		System.out.print(y);      
		
	}

}
