package com.java.sprintQaClass08;

public class Preincrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 50;
		
		int b, c;
		
		b = --a;
		
		b = b--;
		
		c = b;
		
		System.out.println("a = "+a);
		System.out.println("b = "+b);
		System.out.println("c = "+c);

	}

}
