package com.java.sprintQaClass11;

public class Revision {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=20;i++) {
			
			if(i%3==0) {
				System.out.println(i);
			}	
			
		}
		
		for(int j=0;j<=20;j++) {
			if(j%5==0) {
				System.out.println(j);
			}
			
		}
	}

}
